const fixedHeaderWhenScrolling=()=>{const e=document.querySelector(".header");window.scrollY>0?e.classList.add("header--fixed"):e.classList.remove("header--fixed")},openCloseSidebar=()=>{const e=document.querySelector(".header__burger"),d=document.querySelector(".sidebar__close"),n=document.querySelector(".sidebar"),o=e=>{document.body.style.overflow=e?"hidden":""};e.addEventListener("click",(()=>{n.classList.add("sidebar--open"),o(!0)})),d.addEventListener("click",(()=>{n.classList.remove("sidebar--open"),o(!1)})),document.addEventListener("click",(d=>{n.contains(d.target)||e.contains(d.target)||(n.classList.remove("sidebar--open"),o(!1))}))};document.addEventListener("DOMContentLoaded",(()=>{window.addEventListener("scroll",(function(){fixedHeaderWhenScrolling()})),openCloseSidebar()}));